import React from 'react'
import {Container ,Row ,Col} from 'react-bootstrap';
import logo from "../images/logo.png"
import "../index.css"
import axios from 'axios';
export default function NavBar() {
  const [pageCount, setpageCount] = useState(0);
    const onSearch = (word) => {
        search(word)
      }
       //to search in api
  const search = async (word) => {
    if (word === "") {
      
    } else {
      const res = await axios.get(`https://api.themoviedb.org/3/search/movie?api_key=52ef927bbeb21980cd91386a29403c78&query=${word}&language=en`)
      // setMovies(res.data.results)
      console.log(res.data)
      setpageCount(res.data.total_pages)
    }
  }
  return (
    <div className='nav-style w-100'>
      <Container >
        <Row className='pt-2'>
        <Col xs="2" lg="1">
        <a href='/'>
            <img className="logo" src={logo} alt="err"/>
        </a>
        </Col>
        <Col xs="10" lg="11" className=" d-flex align-items-center">
        <div className="search w-100">
            <i className='fa fa-search'></i>
            <input onChange={(e)=> onSearch(e.target.value)}type="text" className="form-control" placeholder="ابحث"/>
        </div>
        </Col>
        </Row>
      </Container> 
    </div>
  );
}
